mapper.select:
select t1.*, (select t2.name from sys_org t2 where t2.id = t1.org_id) orgName
from sys_user t1 left join data_project_user_rel t3 ON t1.id=t3.user_id where t1.super_admin = 0 and t1.deleted = 0

    and t1.username = #{username}


    and t1.mobile = #{mobile}


    and t1.gender = #{gender}

select t1.* from sys_user t1 left join sys_user_role t2 on t1.id = t2.user_id
where t1.deleted = 0 and t2.deleted = 0 and t2.role_id = #{roleId}

    and t1.username = #{username}


    and t1.mobile = #{mobile}


    and t1.gender = #{gender}


    and t2.role_id = #{roleId}

select t1.*, (select t2.name from sys_org t2 where t2.id = t1.org_id) orgName
from sys_user t1 inner join data_project_user_rel t3 ON t1.id=t3.user_id where t1.super_admin = 0 and t1.deleted = 0

    and t1.username = #{username}


    and t1.mobile = #{mobile}


    and t1.gender = #{gender}


    and t3.data_project_id = #{projectId}
